import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import Modal from 'react-modal';

const App = () => {
  const [modalIsOpen, setModalIsOpen] = useState(false)
  return (
    <div className="App">
      <button onClick={() => setModalIsOpen(true)}>რეგისტრაცია</button>
      <Modal isOpen={modalIsOpen}>
        <h2>Title</h2>
        <p>content</p>
        <div>
          <button onClick={() => setModalIsOpen(false)}>დახურვა</button>
        </div>

        style={{
          overlay: {
            position: 'fixed',
            top: '50px',
            left: 0,
            right: 0,
            bottom: 0,
          },
          content: {
/*            position: 'absolute',
            top: '40px',
            left: '40px',
            right: '40px',
            bottom: '40px',
            border: '1px solid #ccc',
            background: '#fff',
            overflow: 'auto',
            WebkitOverflowScrolling: 'touch',
            borderRadius: '4px',
            outline: 'none',
            padding: '20px'*/
          }
        }}
      </Modal>
    </div>
  )
}

export default App
